                        // Èò≤ÂëÜÔºöÁ¢∫Ë™ç video Â≠òÂú®‰∏îÁï´Èù¢Â∑≤Ê∫ñÂÇôÂ•ΩÔºàreadyState >= 2Ôºâ
                        if (!video || video.readyState < 2) {
                            console.warn('Ë∑≥ÈÅéÂ∞öÊú™Ê∫ñÂÇôÂ•ΩÁöÑ video Áï´Èù¢');
                            return;
                        }

                        const flippedCanvas = document.createElement('canvas');
                        flippedCanvas.width = 640;
                        flippedCanvas.height = 480;
                        const flippedCtx = flippedCanvas.getContext('2d');

                        flippedCtx.translate(640, 0);
                        flippedCtx.scale(-1, 1);
                        flippedCtx.drawImage(video, 0, 0, 640, 480);

                        await hands.send({ image: flippedCanvas });
                    },
                    width: 640,
                    height: 480,
                });
                camera.start();
            } catch (error) {
                console.error('Èè°È†≠ÈñãÂïüÂ§±Êïó:', error);
                alert('ÁÑ°Ê≥ïÈñãÂïüÈè°È†≠ÔºåË´ãÁ¢∫Ë™çÂ∑≤Êéà‰∫àÊîùÂΩ±Ê©üÊ¨äÈôê„ÄÇ');
            }
        };

        setupCamera();

        return () => {
            if (streamRef.current) {
                streamRef.current.getTracks().forEach(track => track.stop());
            }

            if (recognitionIntervalRef.current) {
                clearInterval(recognitionIntervalRef.current);
                recognitionIntervalRef.current = null;
            }
        };
