            const response = await fetch('/api/sign-language-recognition/frame', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ keypoints: keypointsArray })
            });
            if (!response.ok) {
                throw new Error(`‰º∫ÊúçÂô®ÈåØË™§Ôºö ${response.status}`);
            }

            const data = await response.json();
            console.log('Ëæ®Ë≠òÁµêÊûúÔºö', data);

            if (data.success && data.text) {
                // ÂâçÁ´ØÈ°ØÁ§∫ÈÇèËºØËàáÂæåÁ´Ø‰∏ÄËá¥ÔºåÈÅøÂÖçÈáçË§áÈ°ØÁ§∫ÈùûÊï∏Â≠óË©û
                if (data.text === 'Ëº∏ÂÖ•ÂÆåÊàê' && data.raw_label) {
                    setResult(prev => prev + '\n' + data.raw_label);
                } else {
                    // Ëã•ÁÇ∫Êï∏Â≠óÊàñËàá‰∏ä‰∏ÄÂÄã‰∏çÂêåÊâçÂä†ÂÖ•
                    const isDigit = /^\d$/.test(data.text);
                    if (isDigit || data.text !== lastRecognizedTextRef.current) {
                        lastRecognizedTextRef.current = data.text;
                        setResult(prev => prev + data.text);
                    }
                }
            }
        } catch (err) {
             console.error('ÁôºÈÄÅÁØÄÈªûÂà∞ÂæåÁ´ØÊôÇÁôºÁîüÈåØË™§Ôºö', err);
        }
    };

    const updateRecognizedText = (newText) => {
        if (!newText || newText.trim() === '') return;
        setResult(prevText => {
            const lastText = lastRecognizedTextRef.current;
            if (newText === "Ë´ãÊâì‰∏ã‰∏ÄÂÄãÂ≠ó") {
                return prevText + '\nË´ãÊâì‰∏ã‰∏ÄÂÄãÂ≠ó';
            }
            if (newText === lastText) {
                return prevText;
            }
             return prevText + newText;
        });
    };

    const handleStartRecording = () => {
        if (!streamRef.current) {
            alert('Èè°È†≠Â∞öÊú™Ê∫ñÂÇôÂ∞±Á∑í');
            return;
        }

        setResult('');
        lastRecognizedTextRef.current = '';
        recordedChunksRef.current = [];

        const mediaRecorder = new MediaRecorder(streamRef.current, { mimeType: 'video/webm' });

        mediaRecorder.ondataavailable = event => {
            if (event.data.size > 0) {
                recordedChunksRef.current.push(event.data);
                console.log(`Êî∂Âà∞ÈåÑË£ΩÁâáÊÆµ: ${event.data.size} bytes`);
            }
        };

        mediaRecorder.onstop = async () => {
            if (recognitionIntervalRef.current) {
                clearInterval(recognitionIntervalRef.current);
                recognitionIntervalRef.current = null;
            }
            try {
                const blob = new Blob(recordedChunksRef.current, { type: 'video/webm' });
                if (blob.size === 0) throw new Error('Ë¶ñË®äÊ™îÊ°àÁÇ∫Á©∫');
                // await uploadVideoToServer(blob); // ‰∏äÂÇ≥ÂÆåÊï¥ÂΩ±Áâá

                setTimeout(() => {
                    setRecognitionStatus('idle');
                    // Áç≤ÂæóÊúÄÊñ∞ÂÄº
                    setResult(currentResult => {
                        console.log('üéØ ÊúÄÁµÇËæ®Ë≠òÁµêÊûú:', currentResult);

                        if (editMessageID) {
                            editMessage(editMessageID, currentResult);
                        } else {
                            addMessage(currentResult, 'customer');
                        }

                        navigate('/conversation');
                        return currentResult; // ‰øùÊåÅÂéüÂÄº‰∏çËÆä
                    });
                }, 1000);
            } catch (err) {
                console.error('ËôïÁêÜÈåÑË£ΩÈåØË™§:', err);
                alert('Ëæ®Ë≠òÂ§±Êïó: ' + err.message);
                setRecognitionStatus('idle');
            }
        };

        mediaRecorderRef.current = mediaRecorder;
        mediaRecorder.start(1000);
        setIsRecording(true);
        setRecognitionStatus('recording');
        startPeriodicRecognition();
    };

    const handleStopRecording = () => {
        if (mediaRecorderRef.current && mediaRecorderRef.current.state !== 'inactive') {
            mediaRecorderRef.current.stop();
        }

        if (recognitionIntervalRef.current) {
            clearInterval(recognitionIntervalRef.current);
            recognitionIntervalRef.current = null;
        }

        setIsRecording(false);
        setRecognitionStatus('processing');
    };

    // Êï¥ÊÆµÂΩ±Áâá‰∏äÂÇ≥ÂæåÁ´ØÂäüËÉΩ
    /**
    const uploadVideoToServer = async (videoBlob) => {
        const formData = new FormData();
        formData.append('video', videoBlob, 'sign-language-recording.webm');

        console.log('ÈñãÂßã‰∏äÂÇ≥Ë¶ñË®äÊ™îÊ°àÂà∞ /api/upload/video');
        const response = await fetch('http://localhost:5000/api/upload/video', {
            method: 'POST',
            body: formData,
            mode: 'cors',
        });

        if (!response.ok) {
            const text = await response.text();
