                />
                <canvas
                    ref={canvasRef}
                    className="overlay-canvas"
                    width={640}
                    height={480}
                />
                {isProcessing && (
                    <div className='processing-indicator'>
                        Ëæ®Ë≠ò‰∏≠...
                    </div>
                )}
            </div>
            <div className={`recognition-result ${isRecording ? 'visible' : 'hidden'}`}>
                <h3>Âç≥ÊôÇËæ®Ë≠òÁµêÊûúÔºö</h3>
                <div
