import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Header from './Header';

const AuthScreen = () => {
    const navigate = useNavigate();
    const [step, setStep] = useState(1);       // 1: 初始畫面, 2: 選擇分行畫面

    const handleStartService = () => {
        setStep(2);
    };

    const handleChoose = () => {
        navigate('/conversation');
    };

    return (
        <div className='auth-screen'>
            <Header showBackButton = {false} />
            {step === 1 ? (
                <div className="initial-content">
                    <ul className='branch-dropdown'>
                        <li>113 台北士林分行</li>
                        <li>135 台北社子分行</li>
                        <li>124</li>
                        <li>116</li>
                        <li>148</li>
                        <li>127</li>
                    </ul>
                    <button 
                        className="primary-button start-button"
                        onClick={handleStartService}
                    >
                        確認 Confirm
                    </button>
                </div>
            ) : (
                <div className="login-content">
                    <h2>身份驗證</h2>
                    <div className="verification-form">
                        <div className="form-group">
                            <label>請輸入身分證號碼：</label>
                            <input type="text" placeholder="A123456789" />
                        </div>
                        <div className="form-group">
                            <label>請輸入電話號碼：</label>
                            <input type="tel" placeholder="09xxxxxxxx" />
                        </div>
                        <button 
                        className="primary-button verify-button"
                        onClick={handleVerify}
                        >
                        確認
                        </button>
                    </div>
                </div>
            )}
            <footer className="auth-footer">
                <p>永豐銀行 © 2025 版權所有</p>
            </footer>
        </div>   
    );
}

export default AuthScreen;
