import React, {useState, useEffect} from 'react';
import {BrowserRouter as Router, Routes, Route, Navigate} from 'react-router-dom';
import AuthScreen from './components/AuthScreen';
import ConversationScreen from './components/ConversationScreen';
import FeedbackScreen from './components/FeedbackScreen';
import ScreenSaver from './components/ScreenSaver';
import SignLanguageRecognition from './components/SignLanguageRecognition';
import SpeechRecognition from './components/SpeechRecognition';
import TextEditor from './components/TextEditor';
import ThankYouScreen from './components/ThankYouScreen';
import {AppProvider} from './contexts/AppContext';
import './App.jsx';
// import './styles/styles.css';

function App() {
    return (
        <div className='App'>
            <AppProvider>
            <Router>
                <Routes>
                    <Route path = '/' element = {<AuthScreen />} />
                    <Route path = '/screensaver' element = {<ScreenSaver />} />
                    <Route path = '/conversation' element = {<ConversationScreen />} />
                    <Route path = '/feedback' element = {<FeedbackScreen />} />
                    <Route path = '/sign-language-recognition' element = {<SignLanguageRecognition />} />
                    <Route path = '/speech-recognition' element = {<SpeechRecognition />} />
                    <Route path = '/text-editor' element = {<TextEditor />} />
                    <Route path = '/thank-you' element = {<ThankYouScreen />} />
                    <Route path = '*' element = {<Navigate to = '/' replace />} />
                </Routes>
            </Router>
        </AppProvider>
        </div>
    );
}

export default App;
