import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from "react-router-dom";
import Header from "./Header.js";
import { useAppContext } from "../contexts/AppContext";

const TextEditor = () => {
    const navigate = useNavigate();
    const location = useLocation();
    const { conversations, editMessage } = useAppContext();
    const [text, setText] = useState('');
    const messageID = location.state?.messageID;

    // 載入現有訊息
    useEffect(() => {
        if(messageID) {
            const message = conversations.find(msg => msg.id === messageID);
            if(message) {
                setText(message.text);
            }
        }
    }, [ messageID, conversations]);

    // 處理文字編輯
    const handleTextChange = (e) => {
        setText(e.target.value);
    };

    // 處理提交
    const handleSubmit = (e) => {
        e.preventDefault();
        if (text.trim() && messageId) {
          editMessage(messageId, text);
          navigate('/conversation');
        }
    };

    // 處理取消
    const handleCancel = () => {
        navigate('/conversation');
    };

    // 處理按鍵事件 (Enter 鍵提交)
    const handleKeyDown = (e) => {
        if (e.key === 'Enter' && !e.shiftKey) {
            e.preventDefault();
            handleSubmit(e);
        }
    };

    return (
        <div className="text-editor-screen">
            <Header title="編輯文字" onBack={handleCancel} />

            <div className="editor-container">
                <form onSubmit={handleSubmit}>
                <div className="text-area-container">
                    <textarea 
                    value={text}
                    onChange={handleTextChange}
                    onKeyDown={handleKeyDown}
                    placeholder="請輸入或編輯文字..."
                    autoFocus
                    />
                </div>

                <div className="keyboard-instructions">
                    <p>按下 Enter 鍵完成編輯</p>
                </div>
                </form>
            </div>
        </div>
    );
};

export default TextEditor;
