import '../styles/SignLanguageRecognition.css';

const SignLanguageRecognition = () => {
    const navigate = useNavigate();
    const location = useLocation();
    const { addMessage, editMessage, setRecognitionStatus, recognitionStatus } = useAppContext();
    const [ isRecording, setIsRecording ] = useState(false);
    const [ result, setResult ] = useState('');
    const videoRef = useRef(null);
    const mediaRecorderRef = useRef(null);
    const recordedChuncksRef = useRef([]);
    const streamRef = useRef(null);
    const editMessageID = location.state?.messageID;
    const { selectedBranch } = location.state || {};

    // Ê®°Êì¨Èè°È†≠ÂïüÂãï
    useEffect(() => {
        const setupCamera = async () => {
            try {
                if (navigator.mediaDevices && navigator.mediaDevices.getUserMedia) {
                    const stream = await navigator.mediaDevices.getUserMedia({video: true});
                    streamRef.current = stream;
                    if (videoRef.current) {
                        videoRef.current.srcObject = stream;
                    }
                }
            } catch (error) {
                console.error('Èè°È†≠ÈñãÂïüÂ§±Êïó:', error);
                alert('ÁÑ°Ê≥ïÈñãÂïüÈè°È†≠ÔºåË´ãÁ¢∫Ë™çÊÇ®Â∑≤Êéà‰∫àÊîùÂΩ±Ê©üÂ≠òÂèñÊ¨äÈôê„ÄÇ');
            }
        };

        setupCamera();

        // Ê∏ÖÁêÜÂáΩÊï∏
        return () => {
            if(streamRef.current) {
                const tracks = streamRef.current.getTracks();
                tracks.forEach(track => track.stop());
            }
        };
    }, []);

    // Ê∏¨Ë©¶ËàáÂæåÁ´ØÁöÑÈÄ£Êé•
    useEffect(() => {
        console.log('ÈñãÂßãÊ∏¨Ë©¶ËàáÂæåÁ´ØÁöÑÈÄ£Êé•...');
        fetch('http://localhost:8080/api/test')
            .then(response => {
                console.log('Êî∂Âà∞ÂæåÁ´ØÂõûÊáâ:', response.status);
                return response.json();
            })
            .then(data => console.log('ÂæåÁ´ØÈÄ£Êé•Ê∏¨Ë©¶ÊàêÂäü:', data))
            .catch(error => console.error('ÂæåÁ´ØÈÄ£Êé•ÈåØË™§:', error));
    }, []);

    // ÊâãË™ûËæ®Ë≠òÊ®°Êì¨ÂõûÊáâ
    /*
    useEffect(() => {
        if(isRecording) {
            // Ê®°Êì¨ÊâãË™ûËæ®Ë≠òÁµêÊûú
            const timer = setTimeout(() => {
                setResult('ÔºàÊ®°Êì¨ÔºâÔºöÊàëË¶ÅËæ¶ÁêÜÂ≠òÊ¨æ„ÄÇ');
            }, 1500);

            return () => clearTimeout(timer);
        }
    }, [isRecording]);*/

    // ÈñãÂßãÈåÑË£Ω
    const handleStartRecording = () => {
        if (!streamRef.current) {
            alert('Èè°È†≠Â∞öÊú™Ê∫ñÂÇôÂ∞±Á∑íÔºåË´ãÁ®çÂæåÂÜçË©¶„ÄÇ');
            return;
        }

        recordedChuncksRef.current = [];
        try {
            const mediaRecorder = new MediaRecorder(streamRef.current, {
                mimeType: 'video/webm;codecs=vp9'
            });

            mediaRecorder.ondataavailable = (event) => {
                if (event.data && event.data.size > 0) {
                    recordedChuncksRef.current.push(event.data);
                    console.log(`Êî∂Âà∞ÈåÑË£ΩÁâáÊÆµ: ${event.data.size} ‰ΩçÂÖÉÁµÑ`);
                }
            };

            mediaRecorderRef.current = mediaRecorder;
            mediaRecorder.start(1000); // ÊØèÁßíËß∏Áôº‰∏ÄÊ¨° dataavailable ‰∫ã‰ª∂
            console.log('ÈñãÂßãÈåÑË£ΩË¶ñË®ä');
            setIsRecording(true);
            setRecognitionStatus('recording');
            setResult('');
        } catch (error) {
            console.error('ÂïüÂãïÈåÑË£ΩÊôÇÁôºÁîüÈåØË™§:', error);
            alert(`ÁÑ°Ê≥ïÈñãÂßãÈåÑË£Ω: ${error.message}`);
        }
    };

    // ÂÅúÊ≠¢ÈåÑË£Ω
    const handleStopRecording = () => {
        if (!mediaRecorderRef.current || mediaRecorderRef.current.state === 'inactive') {
            console.warn('Ê≤íÊúâÈÄ≤Ë°å‰∏≠ÁöÑÈåÑË£Ω');
            return;
        }

        console.log('ÂÅúÊ≠¢ÈåÑË£Ω');
        mediaRecorderRef.current.stop();
        setIsRecording(false);
        setRecognitionStatus('processing');

        // ËôïÁêÜÈåÑÂ•ΩÁöÑÂΩ±ÂÉè
        mediaRecorderRef.current.onstop = async () => {
            try {
                console.log(`ÈåÑË£ΩÂÆåÊàêÔºåÂÖ± ${recordedChuncksRef.current.length} ÂÄãÁâáÊÆµ`);
                if (recordedChuncksRef.current.length === 0) {
                    throw new Error('Êú™Êî∂Âà∞‰ªª‰ΩïË¶ñË®äË≥áÊñô');
                }

                // ÂâµÂª∫ Blob
                const blob = new Blob(recordedChuncksRef.current, { type: 'video/webm' });
                console.log(`Ë¶ñË®äÊ™îÊ°àÂ§ßÂ∞è: ${blob.size} ‰ΩçÂÖÉÁµÑ`);

