                    if (editMessageID) {
                        editMessage(editMessageID, result);
                    } else {
                        addMessage(result, 'customer');
                    }

                    // Âõû conversation page
                    navigate('/conversation', { state: { selectedBranch } });
                }, 1500);
            } catch (error) {
                console.error('ËôïÁêÜÈåÑË£ΩË¶ñË®äÂ§±ÊïóÔºö', error);
                setRecognitionStatus('idle');
                alert('ËôïÁêÜË¶ñË®äÂ§±ÊïóÔºåË´ãÈáçË©¶: ' + error.message);
